import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators, FormBuilder, ValidatorFn } from '@angular/forms';
import { Router } from '@angular/router';
import { DataService } from 'src/app/services/data.service';
@Component({
  selector: 'app-signup',
  templateUrl: './signup.component.html',
  styleUrls: ['./signup.component.css']
})
export class SignupComponent implements OnInit {
  myform: FormGroup;
  submit: boolean = false;
  valid: boolean = false;
  pattern: boolean =  false;
  constructor(private router: Router, private fb: FormBuilder, private service: DataService) {

  }
  // this.myform =this.fb.group({
  //   firstname: ['',[Validators.required]],
  //   email: new FormControl('',Validators.required),
  //   password: new FormControl('',Validators.required),
  //   mobile: new FormControl('',Validators.required),
  //   address: new FormControl('',Validators.required),
  //   confirm: new FormControl('',Validators.required),
  // })
  ngOnInit(): void {
    this.service.getdata().subscribe((data) => {
      console.log(data);
    });
    this.myform = this.fb.group({
      firstname: ['', [Validators.required, Validators.minLength(3)]],
      email: ['', [Validators.required, Validators.email]],
      password: ['', [Validators.required, Validators.minLength(4)]],
      mobile: ['', [Validators.required, Validators.maxLength(10), Validators.minLength(10), Validators.pattern(/^-?(0|[1-9]\d*)?$/)]],
      address: ['', [Validators.required, Validators.minLength(4)]],
      confirm: ['', [Validators.required, Validators.minLength(4)]],
    })
  }

  onSubmit() {
    this.submit = true;
    console.log(this.myform);
    console.log(this.myform.get('firstname'));
    // console.log(this.myform.value.password == this.myform.value.confirm);
    if (!/^-?(0|[1-9]\d*)?$/.test(this.myform.value.mobile.trim())) {
     this.pattern = true;
    }else
    {
      this.pattern = false;
    }

    if (this.myform.valid && (this.myform.value.password == this.myform.value.confirm)) {
      this.service.senddata(this.myform.value).subscribe((data) => {
        console.log(data);
        if (data == "success") {
          this.router.navigateByUrl("/login");
        }
      })
    } else {
      alert("form is invalid")
    }

  }
}
<div class="container-fluid">
  <div class="row">
    <div class="col-12">
      <form (ngSubmit)="onSubmit()" [formGroup]="myform">
        <div class="row">
          <div class="header">
            SIGNUP
          </div>
          <div class="col-6">
            <div class="mb-3">
              <label for="" class="form-label">Name</label>
              <input type="text" class="form-control" name="" id="" aria-describedby="helpId" formControlName="firstname" placeholder="enter name">
              <small id="helpId" *ngIf="myform.value.firstname.trim().length != 0 && myform.value.firstname.trim().length < 3  && submit" class="form-text text-danger">! Name must be above 3 letters</small>
              <small id="helpId" *ngIf="(myform.get('firstname').hasError('required') || myform.value.firstname.trim().length == 0)&& submit" class="form-text text-danger">! field is required</small>
            </div>
            <div class="mb-3">
              <label for="" class="form-label">Email</label>
              <input type="email" class="form-control" name="" id="" formControlName="email" aria-describedby="emailHelpId"
                placeholder="abc@mail.com">
                <small id="helpId" *ngIf="myform.value.email.trim().length != 0 && myform.get('email').hasError('email') && submit" class="form-text text-danger">! Invalid Email</small>
                <small id="helpId" *ngIf="(myform.get('email').hasError('required') || myform.value.email.trim().length == 0)&& submit" class="form-text text-danger">! field is required</small>
            </div>
            <div class="mb-3">
              <label for="" class="form-label">Password</label>
              <input type="password" class="form-control" name="" id="" formControlName="password" aria-describedby="helpId"
                placeholder="enter Password">
                <small id="helpId" *ngIf="myform.value.password.trim().length != 0 && myform.value.password.trim().length < 4  && submit" class="form-text text-danger">! Password must be above 4 letters</small>
                <small id="helpId" *ngIf="(myform.get('password').hasError('required') || myform.value.password.trim().length == 0 )&& submit" class="form-text text-danger">! field is required</small>
              <!-- <small id="helpId" class="form-text text-muted">Help text</small> -->
            </div>
          </div>
          <div class="col-6">
            <div class="mb-3">
              <label for="" class="form-label">Mobile</label>
              <input type="text" class="form-control" name="" id="" formControlName="mobile" aria-describedby="helpId"  placeholder="">
              <!-- <small id="helpId" class="form-text text-muted">Help text</small> -->
              <small id="helpId" *ngIf="!pattern && myform.value.mobile.trim().length != 0 && myform.value.mobile.trim().length != 10 &&  submit" class="form-text text-danger">! Mobile must be 10 Numbers</small>
              <small id="helpId" *ngIf="(myform.get('mobile').hasError('required') || myform.value.mobile.trim().length == 0) && submit" class="form-text text-danger">! field is required</small>
              <small id="helpId" *ngIf="myform.value.mobile.trim().length != 0 && pattern && submit" class="form-text text-danger">! Invalid Mobile</small>
            </div>
            <div class="mb-3">
              <label for="" class="form-label">Address</label>
              <textarea class="form-control" name="" id="" rows="2" formControlName="address"></textarea>
              <small id="helpId" *ngIf="myform.value.address.trim().length != 0 && myform.value.address.trim().length < 4  && submit" class="form-text text-danger">! address must be above 4 letters</small>
              <small id="helpId" *ngIf="(myform.get('address').hasError('required') || myform.value.address.trim().length == 0) && submit" class="form-text text-danger">! field is required</small>
            </div>
            <div class="mb-3">
              <label for="" class="form-label">Confirm Password</label>
              <input type="password" class="form-control" name="" id="" formControlName="confirm" placeholder="enter confirm password">
              <small id="helpId" *ngIf=" myform.value.confirm.trim().length != 0 &&  myform.value.confirm.trim().length < 3 && submit" class="form-text text-danger">! confirm password must be above 3 letters</small>
              <small id="helpId" *ngIf="(myform.get('confirm').hasError('required') || myform.value.confirm.trim().length == 0)&& submit" class="form-text text-danger">! field is required</small>
              <small id="helpId" *ngIf="!myform.get('confirm').hasError('required') && myform.value.confirm.trim().length >= 3 && myform.value.password != myform.value.confirm && submit" class="form-text text-danger">! Please Enter same password</small>
            </div>
          </div>
        </div>
        <div class="mt-2">
          <button type="submit" class="btn btn-primary">Submit</button>
        </div>
        <div class="mt-3 d-flex justify-content-center">
          <small id="helpId" class="form-text text-light">Already Have a Account Please Go <a routerLink="/login"
              routerLinkActive="router-link-active" class="text-info"
              style="text-decoration: none;"><b>Login</b></a></small>
        </div>
      </form>
    </div>
  </div>
</div>
