import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
@Injectable({
  providedIn: 'root'
})
export class DataService {
public API = "http://localhost:3000/"
  constructor(private http:HttpClient) { }
   headers= new HttpHeaders({'Content-Type': 'application/json'});
  httpOptions = {
    headers: this.headers
  };
 senddata(myform){
   return this.http.post(this.API+"signup",[{"name":myform.firstname,"email":myform.email,"mobile":myform.mobile,"password":myform.password,"address":myform.address}],{responseType:'text',headers:this.headers})
 }
 getdata(){
  return this.http.get(this.API,{responseType:"text"});
 }
 login(myform){
  return this.http.post(this.API+"login",[{"name":myform.name,"password":myform.password}],{responseType:'text',headers:headers})
 }
 getdetails(){
  return this.http.get(this.API+"details");
 }
 update(myform,id:number){
  let headers= new HttpHeaders({'Content-Type': 'application/json'});
  const httpOptions = {
    headers: headers
  };
   return this.http.post(this.API+"update",{"name":myform.name,"Mobile":myform.Mobile,"Address":myform.Address,"LastName":myform.LastName,"Id":id},{responseType:'text',headers:headers})
 }
 createuser(myform){

   return this.http.post(this.API+"newdata",[{"name":myform.name,"Mobile":myform.Mobile,"Address":myform.Address,"LastName":myform.LastName}],{responseType:'text',headers:headers})
 }
 delete(id){
  let headers= new HttpHeaders({'Content-Type': 'application/json'});
  const httpOptions = {
    headers: headers
  };
 return this.http.post(this.API+"delete",{"Id":id},{responseType:'text',headers:headers})
 }
}
