import { Component } from '@angular/core';
import { NgForm } from '@angular/forms';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  constructor(private router: Router) {

  }
  firstname:boolean=true;
  onSubmit(form:NgForm) {
   // alert();
    console.log(form.value.name);
    if(form.value.name == ""){
      firstname = false;
    }
  }
}
<div class="container-fluid">
  <div class="row">
    <div class="col-12">
      <div class="row">
        <div class="col-2"></div>
        <div class="col-8">
          <div class="login">
            <div class="header">
              LOGIN
            </div>
            <form (ngSubmit)="onSubmit(form)" #form="ngForm">
              <div class="mb-3">
                <label for="" class="form-label">Name</label>
                <input type="text" class="form-control" name="name" id="" aria-describedby="helpId" placeholder="" required ngModel>
                <!-- <small id="helpId" class="form-text text-muted">Help text</small> -->
                <small id="helpId" class="form-text text-danger">! Field Is Required</small>
              </div>
              <div class="mb-3">
                <label for="" class="form-label">Password</label>
                <input type="text" class="form-control" name="password" id="" aria-describedby="helpId" placeholder="" required ngModel>
                <small id="helpId" class="form-text text-danger">! Field Is Required</small>
              </div>
              <div class="mt-2">
                <button type="submit" class="btn btn-primary" >Submit</button>

              </div>
              <div class="mt-3 d-flex justify-content-center">
                <small id="helpId" class="form-text text-light">Dont Hava a Account Please <a routerLink="/signup"
                    routerLinkActive="router-link-active" class="text-info"
                    style="text-decoration: none;"><b>signup</b></a></small>
              </div>
            </form>
          </div>
        </div>
        <div class="col-2"></div>
      </div>
    </div>
  </div>


</div>
