    <div class="row">
      <div class="col-12">
        <table class="table table-bordered text-center">
          <thead class="bg-dark text-light">
            <tr>
              <th scope="col">S.no</th>
              <th scope="col">Name</th>
              <th scope="col">Last Name</th>
              <th scope="col">Mobile</th>
              <th scope="col">Address</th>
              <th scope="col">Actions</th>
            </tr>
          </thead>
          <tbody>
           <tr *ngFor="let data of datas">
            <td>{{data.Id}}</td>
            <td>{{data.name | uppercase}}</td>
            <td>{{data.LastName | uppercase}}</td>
            <td>{{data.Mobile}}</td>
            <td>{{data.Address | uppercase}}</td>
            <td><a class="btn btn-sm btn-primary" data-bs-toggle="modal" data-bs-target="#modalId" (click)="getdet(data.Id)">Update</a> &nbsp;&nbsp;<a class="btn btn-sm btn-danger" (click)="delete(data.Id)">Delete</a></td>
           </tr>
          </tbody>
        </table>
      </div>
    </div>
  </div>

