          <div class="mb-3" >
            <label for="" class="form-label">Name</label>
            <input type="text"
              class="form-control" formControlName="name" name="" id="name" aria-describedby="helpId" placeholder="enter name">
            <!-- <small id="helpId" class="form-text text-muted">Help text</small> -->
            <small id="helpId" *ngIf="profileForm.get('name').hasError('required') && profileForm.get('name').touched" class="form-text text-danger">! field is required</small>
          </div>
          <div class="mb-3">
